import './AddRecipes.css'
import React from "react"

const AddRecipes = (props) => {
    return (
        <div>
            <h2>Añade nueva receta</h2>
            <form onSubmit={props.addRecipes}>
                <fieldset>
                    <label>
                        <p>Introduce un nombre:</p>
                        <input type="text" name='nameRecipes' id='namerRecipes' value={props.newRecipe.value} 
                        onChange = {(event) => props.setNewRecipe({
                            ...props.newRecipe,
                            "name": event.target.value
                        })}></input>
                    </label>
                </fieldset>
                <fieldset>
                    <label>
                        <p>Introduce un número de personas:</p>
                        <input type="number" name='numberPeople' id='numberPeople' value={props.newRecipe.value} 
                        onChange = {(event) => props.setNewRecipe({
                            ...props.newRecipe,
                            "serving": event.target.value
                        })}></input>
                    </label>
                </fieldset>
                <fieldset>
                    <label>
                        <p>Introduce la url de la imagen:</p>
                        <input type="text" name='urlImage' id='urlImage' value={props.newRecipe.value} 
                        onChange = {(event) => props.setNewRecipe({
                            ...props.newRecipe,
                            "imageUrl": event.target.value
                        })}></input>
                    </label>
                </fieldset>
                <button type='submit'>Añadir Receta</button>
            </form>
        </div>
    )
}
export default AddRecipes
