import './App.css';
import AddRecipes from './components/AddRecipes/AddRecipes';
import React from 'react';
import RecipesList from './components/RecipesList/RecipesList';
import SelectedRecipes from './components/SelectedRecipes/SelectedRecipes';

const API_URL = "http://localhost:4000/recipes/"

function App() {

  const [recipeList, setRecipeList] = React.useState([])
  const [recipeDetail, setRecipeDetail] = React.useState([])

  React.useEffect(() => {
    getRecipesList()
  },[])


  const addRecipes = (event, newRecipe) =>{
    event.preventDefault()
    fetch(API_URL, {
      method : "POST",
      body : JSON.stringify(newRecipe),
      headers : {
        "Content-Type" : "application/json"
      }
    })
    .then(response => response.json())
    .then(data => {
        getRecipesList()
    })
  }

    const getRecipesList = () => {
      fetch(API_URL)
      .then(response => response.json())
      .then(data => {
        setRecipeList(data)
      })
    }

    const getDetailsRecipe = (id) => {
      fetch(`${API_URL}/${id}`)
      .then(response => response.json())
      .then(data => {
        setRecipeDetail(data)
      })
    }

  return (
    <div className="app">
      <AddRecipes addRecipes={addRecipes}></AddRecipes>
      <RecipesList recipesList={recipeList} getDetailsRecipe={getDetailsRecipe}></RecipesList>
      {(recipeDetail === []) ? <p>Selecciona una receta</p> : <SelectedRecipes recipeDetail={recipeDetail}></SelectedRecipes>}
    </div>
  );
}

export default App;
